mapper.select:
select SUM(t.push_num) FROM t_push_stat t


                t.column_id = #{columnId}


                t.article_id = #{articleId}


                t.push_time >= #{startDate}


                t.push_time <= #{endDate}

select SUM(t.open_num) FROM t_push_stat t


                t.column_id = #{columnId}


                t.article_id = #{articleId}


                t.push_time >= #{startDate}


                t.push_time <= #{endDate}
